. c:\DoIt\Boot\DoIt.boot.ps1 try { $runCondition = @' %run.condition% '@ if (!(Test-RunCondition -RunCondition $runCondition)) { return }
$configFiles = Get-TeamcityArrayParameter -Param @' %config.files% '@
$configValues = Get-TeamcityArrayParameter -Param @' %config.values% '@
$connectionNodes = Get-TeamcityArrayParameter -Param @' %connection.nodes% '@
$configType = '%config.type%' $connectionParams = Get-TeamcityConnectionParameters ` -Nodes $connectionNodes ` -User '%connection.userName%' ` -Password '%connection.password%' ` -UseHttps:%connection.useHttps%
Update-ConfigFile ` -ConfigFiles $configFiles ` -ConfigType $configType ` -ConfigValues $configValues ` -ConnectionParameters $connectionParams } catch { Write-ErrorRecord }
