parameters.param:
. c:\PSCI\Boot\PSCI.boot.ps1 try {
$sourcePaths = Get-TeamcityArrayParameter -Param @' %copyFiles.sourcePaths% '@
$include = Get-TeamcityArrayParameter -Param @' %copyFiles.include% '@
$exclude = Get-TeamcityArrayParameter -Param @' %copyFiles.exclude% '@
$connectionParams = Get-TeamcityConnectionParameters ` -Nodes '%connection.nodes% ` -User '%connection.userName%' ` -Password '%connection.password%' ` -UseHttps:%connection.useHttps%
Invoke-CopyDownloadFilesMetaRunner ` -Path $sourcePaths ` -ConnectionParams $connectionParams ` -Destination '%copyFiles.destinationPath%' ` -Include $include ` -Exclude $exclude ` -ClearDestination:%copyFiles.clearDestination% } catch { Write-ErrorRecord }
