parameters.param:
. c:\PSCI\Boot\PSCI.boot.ps1 try {
$user = '%trendReport.userId%' $pass = '%trendReport.password%' if (!$user -or !$pass) { throw "Either trendReport.userId or trendReport.password is empty. Please modify this metarunner to include valid userId and password." }
$outputFilePrefix = '%trendReport.outputFilePrefix%'
$serverUrl = '%teamcity.serverUrl%' -replace 'http://','' $connectionString = "Server=${serverUrl};Database=TeamCity;User Id=${user};Password=${pass}"
$includeRegex = @' %system.testName.includeRegex% '@
$excludeRegex = @' %system.testName.excludeRegex% '@
$conversionRegex = Get-TeamcityArrayParameter -Param @' %system.testName.regex% '@
$params = @{ TeamcityBuildId = '%teamcity.build.id%' TeamcityDbConnectionString = $connectionString OutputDir = 'TestTrend' OutputCsvName = "TestTrendReport${outputFilePrefix}.csv" OutputHtmlName = "TestTrendReport${outputFilePrefix}.html" InputThresholdCsvPath = '%trendReport.inputThresholdCsv%' TestNameConversionRegex = $conversionRegex TestNameIncludeRegex = $includeRegex TestNameExcludeRegex = $excludeRegex NumberOfLastBuilds = %system.numberOfLastBuilds% GenerateCsvFile = %system.generateCsv% }
New-TeamcityTrendReport @params
Write-Host "##teamcity[publishArtifacts 'TestTrend => TestTrend']" } catch { Write-ErrorRecord }
